mapper.update:
CREATE TABLE IF NOT EXISTS "imgUrl"
(
    "id"      INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
    "path"    TEXT    NOT NULL,
    "project" TEXT    NOT NULL,
    "name"    TEXT    NOT NULL
);

CREATE UNIQUE INDEX "imgPath"
    ON "imgUrl" (
                 "name" ASC,
                 "project"
        );

mapper.delete:
DELETE
FROM "imgUrl"
WHERE project = #{project}
  and name = #{name}

mapper.select:
SELECT path
FROM "imgUrl"
WHERE project = #{project}
  and name = #{name}
